package com.kriit.Util;

import java.io.BufferedInputStream;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import org.apache.log4j.Logger;
import org.apache.poi.hwpf.extractor.WordExtractor;
import org.apache.poi.openxml4j.exceptions.InvalidFormatException;
import org.apache.poi.openxml4j.opc.OPCPackage;
import org.apache.poi.poifs.filesystem.FileMagic;
import org.apache.poi.xwpf.extractor.XWPFWordExtractor;
import org.apache.poi.xwpf.usermodel.XWPFDocument;

/**
 *
 * @author Viral
 */
public class WordContentExtractor {

    final static Logger LOGGER = Logger.getLogger(WordContentExtractor.class);

    public String getDocContent(String input) {
        LOGGER.info("Start WordContentExtractor--->getDocContent()");
        String parsedText;
        byte[] decodedBytes = java.util.Base64.getDecoder().decode(input);
        try {

            parsedText = msWordToString(new ByteArrayInputStream(decodedBytes));

        } catch (IOException ex) {
            parsedText = null;
            LOGGER.info("Exception :: WordContentExtractor-->getDocContent ::" + ex.getMessage());
        } catch (InvalidFormatException ex) {
            parsedText = null;
            LOGGER.info("Exception :: WordContentExtractor-->getDocContent ::" + ex.getMessage());
        }

        LOGGER.info("Word String Length:" + String.valueOf(parsedText != null ? parsedText.length() : 0));
        LOGGER.info("Ends WordContentExtractor--->getDocContent()");
        return parsedText;

    }

    private static String msWordToString(InputStream inputStream) throws IOException, InvalidFormatException {
        String strRet = null;
        try (InputStream wordStream = new BufferedInputStream(inputStream)) {
            if (FileMagic.valueOf(wordStream) == FileMagic.OLE2) {
                WordExtractor extractor = new WordExtractor(wordStream);
                System.out.println("OLE2:");
                strRet = extractor.getText();
                extractor.close();
            } else if (FileMagic.valueOf(wordStream) == FileMagic.OOXML) {
                XWPFDocument doc = new XWPFDocument(OPCPackage.open(wordStream));
                XWPFWordExtractor extractor = new XWPFWordExtractor(doc);
                System.out.println("OOXML:");
                strRet = extractor.getText();
                extractor.close();
            }
        }
        return strRet;
    }

    public static void main(String[] args) {

        //String WORD = "";
        String WORD = "";
        WordContentExtractor pe = new WordContentExtractor();
        String docString = pe.getDocContent(WORD);
        System.out.println("WordContentExtractor --> Main -->Doc Content::" + docString);

    }

}
